import logoImg from "@/assets/img/logo.png";
import { login } from "@/api/user";
import { setToken } from '@/utils/auth';
export default {
  data: function data() {
    return {
      logo: logoImg,
      loginForm: {
        username: 'admin',
        password: '123456'
      },
      rules: {
        username: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 2,
          max: 8,
          message: '长度在 2 到 8 个字符',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }]
      }
    };
  },
  mounted: function mounted() {},
  methods: {
    loginByWechat: function loginByWechat() {},
    showMessage: function showMessage(type, message) {
      this.$message({
        type: type,
        message: message
      });
    },
    submitForm: function submitForm(loginForm) {
      var _this = this;
      this.$refs[loginForm].validate(function (valid) {
        if (valid) {
          var userinfo = _this.loginForm;
          login(userinfo).then(function (res) {
            var userList = res.data.userList;
            setToken("Token", userList.token);
            _this.$router.push({
              path: '/'
            });
            _this.$store.dispatch('initLeftMenu'); //设置左边菜单始终为展开状态
          });
        }
      });
    }
  }
};
