import QRCode from 'qrcodejs2';
import { shareUrl } from "@/utils/env";
export default {
  name: 'infoShare',
  data: function data() {
    return {
      qrcode: {
        show: false
      },
      qrcodeObj: {
        text: shareUrl,
        // 要分享的网页路径
        width: 80,
        height: 80,
        colorDark: '#000000',
        colorLight: '#ffffff',
        correctLevel: QRCode.CorrectLevel.H
      }
    };
  },
  mounted: function mounted() {
    this.creatQrCode();
  },
  methods: {
    showqrcode: function showqrcode() {
      this.qrcode.show = true;
    },
    hideqrcode: function hideqrcode() {
      this.qrcode.show = false;
    },
    creatQrCode: function creatQrCode() {
      var qrcode = new QRCode(this.$refs.qrCodeUrl1, this.qrcodeObj);
    },
    shareToQQ: function shareToQQ() {
      this.$emit('shareToQQ');
    },
    shareToQQzone: function shareToQQzone() {
      this.$emit('shareToQQzone');
    },
    shareToWeibo: function shareToWeibo() {
      this.$emit('shareToWeibo');
    },
    shareToDouban: function shareToDouban() {
      this.$emit('shareToDouban');
    }
  }
};
