 .info_container{
    padding: 20px;
    background: #fff;
    box-sizing: border-box;
    overflow: auto;
 }
 .row{
     margin:20px;
 }
.info_row{
     .area{
        border:1px solid #dfdfdf;
        height:178px;
        overflow:hidden;
         .imgarea{
             text-align:center;
             padding:10px;
             img {
                 width:120px;
                 height:120px;
                 border-radius: 50%;
             }
         }
         .namearea{
             padding:10px;
             font-size:14px;
             p{
                 line-height:24px;
             }
             .awards{
                 text-align:center;
                 width:100%;
                 height:30px;
                 line-height:30px;
                 cursor: pointer;
                 background-color: #3bc5ff;
                 border:1px solid #3bc5ff;
                 color: white;
                 display: block;
             }
             .awards:hover{
                 background-color: #f9c855;
                 border:1px solid #f9c855;
             }
         }
         .moneyarea{
             padding:10px;
             float:left;
             .title{
                 font-size:15px;
                 font-weight:bolder;
                 line-height:50px;
                 .money{
                     color:#c10000;
                     margin-right:5px;
                 }
                 .yuan{
                         font-weight:100;
                         font-size:13px;
                 }
             }

         }
         .chongzhiarea{
                 padding:10px;
                 text-align:center;
                 img{
                     width: 80%;
                     height: 40px;
                 }
                 .chongzhi{
                     margin-top:10px;
                 }
         }
         .tipsarea{
             clear:both;
             font-size:14px;
             padding:10px;
             padding-top:15px;
             a{
                 color:#3bc5ff;
             }
         }
         .dataarea{
              padding:10px;
              text-align:center;
              font-size:14px;
             .gtitle{
                 width:100%;
                 height:30px;
                 line-height:30px;
                 cursor: pointer;
                 background-color: #3bc5ff;
                 color: white;
                 display: block;
             }
             .gdataarea{
                 padding-left: 25px;
                 p{
                     line-height:38px;
                 }
                 .num{
                     font-weight:bolder;
                     color:#c10000;
                 }
                 .title{
                     color:#3bc5ff;
                 }
                 .gdata{
                     margin:10px;
                 }
             }
             .morearea{
                 a{
                     color:#3bc5ff;
                 }
             }
         }
     }
} 
.data_row{
    font-size:18px;
    .area{
        border:1px solid #dfdfdf;
        height:200px;
        overflow:hidden;
        padding:10px;
        text-align:center;
        font-weight:bolder;
         .symbol{
             font-size: 16px;
             font-style: italic;
             font-family: "microsoft yahei";
             margin-right: 10px;
             color:#232323;
             font-weight:100;
         }
        p{
            line-height:70px;
        }
        .num{
            color:#c10000;
        }
        .detail{
            margin-top:5px;
            font-size:14px;
            text-align: center;
            width: 100%;
             height: 30px;
             line-height: 30px;
             cursor: pointer;
             color: white;
             display: block;
             border:1px solid #3bc5ff;
        }
        .dbgcolor{
           background-color: #3bc5ff;
        }
        .zbgcolor{
            color:#3bc5ff;
        }
        .dbgcolor:hover{
             background-color: #fff;
             color:#3bc5ff
        }
        .zbgcolor:hover{
             background-color: #3bc5ff;
             color:#fff;
        }
    }
}
.ban_row{
     .banarea{
         width: 100%;
         display: flex;
         justify-content: space-between;
         li{
             a{
                 background: url(../../assets/img/pro_map.gif?v=1.0) no-repeat;
                 width: 200px;
                 height: 50px;
                 display: block;
             }
         }
     }
}
