  import { getSalesTableList } from "@/api/sales";
  import userImg from "@/assets/img/avatar-3.png";

  export default {
    name:'commentList',
    data() {
      return {
        commentData: [],
        userImg:userImg
      }
    },
    mounted(){
        this.getSalesList();
    },
    methods:{
        // 获取列表数据
        getSalesList(){
            getSalesTableList({}).then(res => {
                let list = res.data.list
                this.commentData = list.slice(0,3);
            })
        },
    }
  }
